/**
 * „Ç®„É™„Ç¢ÊÉÖÂ†±„ÇíÂü∫„Å´„Éï„Ç°„Ç§„É´Âêç„ÇíÁîüÊàê
 * @param areaSelection „Ç®„É™„Ç¢ÈÅ∏ÊäûÊÉÖÂ†±
 * @param ratio Âá¶ÁêÜÂâ≤ÂêàÔºà0.5 = 50%, 1.0 = 100%Ôºâ
 * @returns ÁîüÊàê„Åï„Çå„Åü„Éï„Ç°„Ç§„É´Âêç
 */
function generateAreaBasedFilename(areaSelection, ratio) {
    // „Éï„Ç°„Ç§„É´Âêç„Å´‰∏çÈÅ©Âàá„Å™ÊñáÂ≠ó„ÇíÁΩÆÊèõ„Åô„ÇãÈñ¢Êï∞
    const sanitizeForFilename = (str) => {
        return str
            .replace(/[\/\\\?%\*:|"<>]/g, '') // „Éï„Ç°„Ç§„É´Âêç„Å´‰Ωø„Åà„Å™„ÅÑÊñáÂ≠ó„ÇíÂâäÈô§
            .replace(/\s+/g, '') // „Çπ„Éö„Éº„Çπ„ÇíÂâäÈô§
            .replace(/[\(\)]/g, ''); // Êã¨Âºß„ÇÇÂâäÈô§
    };
    const parts = [];
    // „É°„Ç§„É≥„Ç®„É™„Ç¢Âêç„ÇíËøΩÂä†
    if (areaSelection.mainAreaName) {
        parts.push(sanitizeForFilename(areaSelection.mainAreaName));
    }
    // „Çµ„Éñ„Ç®„É™„Ç¢Âêç„ÇíËøΩÂä†
    if (areaSelection.subAreaName) {
        parts.push(sanitizeForFilename(areaSelection.subAreaName));
    }
    // Ë©≥Á¥∞„Ç®„É™„Ç¢Âêç„ÇíËøΩÂä†
    if (areaSelection.detailAreaName) {
        parts.push(sanitizeForFilename(areaSelection.detailAreaName));
    }
    // Âá¶ÁêÜÂâ≤Âêà„ÇíËøΩÂä†Ôºà50%„Åæ„Åü„ÅØ100%Ôºâ
    if (ratio !== undefined) {
        const percentage = Math.round(ratio * 100);
        parts.push(`${percentage}%`);
    }
    // „Éë„Éº„ÉÑ„ÅåÁ©∫„ÅÆÂ†¥Âêà„ÅØ„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØ
    if (parts.length === 0) {
        const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19);
        return `salon_details_${timestamp}.csv`;
    }
    // "„Ç®„É™„Ç¢_„Ç®„É™„Ç¢„ÅÆË©≥Á¥∞_„Åï„Çâ„Å´Ë©≥Á¥∞.csv" „ÅÆÂΩ¢Âºè„ÅßÁµêÂêà
    return `${parts.join('_')}.csv`;
}
/**
 * CSVÁî®„Å´„Éï„Ç£„Éº„É´„Éâ„Çí„Ç®„Çπ„Ç±„Éº„Éó
 * @param field „Ç®„Çπ„Ç±„Éº„Éó„Åô„Çã„Éï„Ç£„Éº„É´„Éâ
 * @returns „Ç®„Çπ„Ç±„Éº„Éó„Åï„Çå„Åü„Éï„Ç£„Éº„É´„Éâ
 */
function escapeCSVField(field) {
    if (!field)
        return '""';
    // ÊîπË°å„ÄÅ„Ç´„É≥„Éû„ÄÅ„ÉÄ„Éñ„É´„ÇØ„Ç©„Éº„Éà„ÇíÂê´„ÇÄÂ†¥Âêà„ÅØ„ÉÄ„Éñ„É´„ÇØ„Ç©„Éº„Éà„ÅßÂõ≤„ÇÄ
    if (field.includes(',') || field.includes('\n') || field.includes('"')) {
        // „ÉÄ„Éñ„É´„ÇØ„Ç©„Éº„Éà„Çí„Ç®„Çπ„Ç±„Éº„Éó
        const escaped = field.replace(/"/g, '""');
        return `"${escaped}"`;
    }
    // Á©∫ÁôΩ„ÅÆ„Åø„ÅÆÂ†¥Âêà„ÇÑ„Çπ„Éö„Ç∑„É£„É´„Ç±„Éº„Çπ
    if (field.trim() === '') {
        return '""';
    }
    return `"${field}"`;
}
